mapper.update:
update tour
set title_a=#{updateParam.titleA},
    title_b=#{updateParam.titleB},
    title_C=#{updateParam.titleC}
where id=#{id} and date_time = #{dateTime}

mapper.select:
select id, date_time, title_a, title_b, title_c
from tour
where id=#{id}

select id, date_time, title_a, title_b, title_c
from tour
where id = #{id} and date_time = #{dateTime}
