mapper.update:
update tour
set departure=#{updateDto.departure}
    destination=#{updateDto.destination}
    title_a=#{updateDto.titleA},
    title_b=#{updateDto.titleB},
    title_C=#{updateDto.titleC}
where id=#{id} and date_time = #{dateTime}

mapper.select:
select id, date_time, departure, destination, title_a, title_b, title_c
from tour
where id=#{id}

select id, date_time, departure, destination, title_a, title_b, title_c
from tour
where id = #{id} and date_time = #{dateTime}
